package class0909;

public class Code04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'A';
		System.out.println("ch: " + ch);
		
		int num = 100;
		long l = 100L;
		System.out.println("num: " + num + "," + "l: " + l);
		
		float f = 10.123f;
		double d = 10.123; 
		System.out.println("f: " + f + "," + "d: " + d);
		
		boolean b1 = true; 
		boolean b2 = false;
		System.out.println("b1: " + b1 + "," + "b2: " + b2);
	}

}
