package class0909_2;

public class Code06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		a = a + 10;   					//a = 20
		System.out.println(a);
		
		int b = 10;
		b +=10; 						//b = b + 10       
		System.out.println(b); 			//b = 20
		

		System.out.println(a+=5); 		//a = a + 5   25
		System.out.println(a-=5);		//
		System.out.println(a*=5);
		System.out.println(a/=5);
		System.out.println(a%=5);
		
	}

}
